import { Injectable } from '@angular/core';

/**
 * Test file for complexity report: multiple functions with nested,
 * multi-line decision points. TypeScript.
 */
@Injectable({ providedIn: 'root' })
export class ComplexityNestedTestService {
  /**
   * Deeply nested if/else with multi-line blocks and ternaries inside.
   */
  processNestedConditions(
    level: number,
    data: { min?: number; max?: number } = {},
    fallback: number = 0
  ): number {
    const min = data?.min ?? fallback;
    const max = data?.max ?? level;

    if (level <= 0) {
      return min;
    }

    if (level === 1) {
      return max > 0 ? min + 1 : min;
    }

    if (level > 1 && level < 10) {
      if (min < max) {
        return (level % 2 === 0 ? min + level : max - 1) ?? fallback;
      } else if (min === max) {
        return level;
      } else {
        return min > 0 ? min - 1 : 0;
      }
    }

    return level >= 10 ? level : fallback;
  }

  /**
   * Nested loops with conditionals and optional chaining inside.
   */
  aggregateWithNestedLoops(
    matrix: (number[] | null)[],
    threshold: number = 5
  ): number {
    let sum = 0;

    for (let i = 0; i < matrix.length; i += 1) {
      const row = matrix[i];

      if (row == null) continue;

      for (const cell of row) {
        const value = cell ?? 0;
        if (value > threshold) {
          sum += value;
        } else if (value > 0) {
          sum += value < threshold ? value : 0;
        }
      }

      for (const k in row) {
        const v = row[k] ?? 0;
        sum += v > 0 ? v : 0;
      }
    }

    return sum;
  }

  /**
   * Switch inside try/catch with nested if and ternaries; multi-line.
   */
  classifyAndHandle(
    code: string,
    opts: { strict?: boolean } = {}
  ): string {
    const strict = opts?.strict ?? false;

    try {
      switch (code) {
        case 'A':
          if (strict) {
            return code.toLowerCase();
          }
          return code;

        case 'B':
          return strict ? 'b' : 'B';

        case 'C':
          if (!strict) {
            return code;
          }
          return code.toLowerCase();

        default:
          return strict ? code?.toLowerCase() ?? code : code;
      }
    } catch {
      return code ?? 'unknown';
    }
  }

  /**
   * While and do-while with nested conditionals; multiple decision paths.
   */
  iterateWithGuards(
    maxIterations: number = 10,
    skipEven: boolean = false
  ): number[] {
    const results: number[] = [];
    let i = 0;

    while (i < maxIterations) {
      if (skipEven && i % 2 === 0) {
        i += 1;
        continue;
      }

      if (i % 3 === 0) {
        results.push(i);
      } else if (i > 5) {
        results.push(i * 2);
      }

      i += 1;
    }

    let j = 0;
    do {
      const value = j < results.length ? results[j] ?? 0 : 0;
      if (value > 0 && (skipEven ? j % 2 !== 0 : true)) {
        results[j] = value + 1;
      }
      j += 1;
    } while (j < results.length && j < maxIterations);

    return results;
  }

  /**
   * Deep ternary and logical chains across multiple lines.
   */
  resolveValue(
    a: number | null,
    b: number | null,
    c: number | null,
    defaultVal: number = 0
  ): number {
    return (
      a ??
      (b != null
        ? b > 0
          ? b
          : defaultVal
        : c != null && c >= 0
          ? c
          : defaultVal)
    );
  }
}

/**
 * Complexity test file in a deeply nested folder.
 * Path: app/complexity-tests/deep/nested/decision-points/
 */

export type NestedPayload = {
  value?: number;
  children?: NestedPayload[];
  flag?: boolean;
};

/**
 * Recursive-style branching with nested optional chaining and conditionals.
 */
export function evaluateNestedPayload(
  payload: NestedPayload | null,
  depth: number = 0,
  maxDepth: number = 5
): number {
  if (payload == null) {
    return depth;
  }

  const raw = payload?.value ?? 0;
  const children = payload?.children;

  if (depth >= maxDepth) {
    return raw > 0 ? raw : 0;
  }

  if (children?.length === 0) {
    return payload?.flag ? raw + 1 : raw;
  }

  if (children != null && children.length > 0) {
    let sum = raw;
    for (const child of children) {
      const v = child?.value ?? 0;
      if (v > 0) {
        sum += evaluateNestedPayload(
          child,
          depth + 1,
          maxDepth
        );
      } else if (child?.flag) {
        sum += 1;
      }
    }
    return sum;
  }

  return payload?.flag ? raw + depth : raw;
}

/**
 * Switch inside loops with multi-line conditionals.
 */
export function processByKind(
  items: Array<{ kind: string; value?: number }>,
  defaultVal: number = 0
): number[] {
  const results: number[] = [];

  for (let i = 0; i < items.length; i += 1) {
    const item = items[i];
    const val = item?.value ?? defaultVal;

    switch (item?.kind) {
      case 'add':
        results.push(val > 0 ? val + 1 : val);
        break;
      case 'double':
        if (val >= 0) {
          results.push(val * 2);
        } else {
          results.push(val);
        }
        break;
      case 'skip':
        if (val === 0) continue;
        results.push(val);
        break;
      default:
        results.push(val ?? defaultVal);
    }
  }

  return results;
}

/**
 * Deep ternary and logical chains across many lines.
 */
export function resolveDeep(
  a: number | null | undefined,
  b: number | null | undefined,
  c: number | null | undefined,
  d: number = 0
): number {
  return (
    a != null && a >= 0
      ? a
      : b != null
        ? b < 0
          ? d
          : b
        : c != null && c > 0
          ? c
          : d
  );
}

import { Injectable } from '@angular/core';

@Injectable({ providedIn: 'root' })
export class DecisionPointTestService {
  /**
   * Single method that hits every decision point for complexity report testing.
   * Control flow: if, else if, for, for-of, for-in, while, do-while, switch, case, catch.
   * Expressions: ternary, &&, ||, ??, ?.
   * Default parameter.
   */
  runAllDecisionPoints(
    items: string[],
    opts: { flag?: boolean; value?: number } = {},
    defaultValue: number = 10
  ): number {
    let total = 0;
    const val = opts?.value ?? defaultValue;
    const flag = opts?.flag && items.length > 0;

    if (items.length === 0) return 0;
    else if (items.length === 1) total += 1;

    for (let i = 0; i < items.length; i += 1) total += 1;
    for (const item of items) total += item?.length ?? 0;
    for (const k in items) total += items[k]?.length ?? 0;

    let n = 0;
    while (n < 3) {
      total += n;
      n += 1;
    }
    let m = 0;
    do {
      total += m;
      m += 1;
    } while (m < 2);

    switch (items.length) {
      case 0:
        break;
      case 1:
        total += 1;
        break;
      default:
        total += 2;
    }

    try {
      total += flag ? 1 : 0;
    } catch {
      total += 0;
    }

    return total;
  }
}

